<script lang="ts">
import { Vue } from 'vue-class-component';
import * as Theme from './presets';
import { Digits, NumberOptions } from './digits';
import { START_PATH, STOP_PATH } from './start-stop-button-path';

export default class Clock extends Vue {
  theme = Theme.lavender;
  settingsClicked = false;
  isRunning = false;
  leftDigit = new Digits(NumberOptions.LEFT);
  middleDigit = new Digits(NumberOptions.MIDDLE);
  rightDigit = new Digits(NumberOptions.RIGHT);
  mainBtn = START_PATH;
  private readonly queue = [this.leftDigit, this.middleDigit, this.rightDigit];
  private current = 0;
  // private isHovered = false;
  // input3 = '';
  // @Ref('input3Ref') readonly input3Ref!: HTMLInputElement;

  // test(): void {
  // this.input3.focus();
  //   console.log(this.input3);
  //   this.input3Ref.focus();
  // }

  get isAllUp(): boolean {
    return this.queue.every((timer) => timer.isUp);
  }

  private next(): void {
    if (this.isAllUp) {
      return;
    }
    if (this.current === this.queue.length - 1) {
      this.current = 0;
    } else {
      this.current++;
    }
    if (this.queue[this.current].isUp) {
      this.next();
    }
  }

  private start(): void {
    this.queue[this.current].start();
  }

  private pause(): void {
    this.queue[this.current].pause();
    this.next();
  }

  onClickColorChange(): void {
    this.theme = Theme.pickTheme.pick();
  }

  onClick(): void {
    if (this.isRunning) {
      this.mainBtn = START_PATH;
      this.isRunning = false;
      this.pause();
    } else {
      this.mainBtn = STOP_PATH;
      this.isRunning = true;
      this.start();
    }

    // if (this.isHovered) {
    //   this.onMouseEnter();
    // }
  }

  onClickSettings(): void {
    this.settingsClicked = !this.settingsClicked;
  }

  // onMouseEnter(): void {
  //   this.mainBtn1.color = this.mainBtn1.hoverColor;
  //   this.isHovered = true;
  // }

  // onMouseLeave(): void {
  //   if (this.isRunning) {
  //     this.applyStop();
  //   } else {
  //     this.applyStart();
  //   }
  //   this.isHovered = false;
  // }
}
</script>
